import React, { Component } from "react";
import NameIntro from "./components/nameintro";
import LatestArticle from "./components/latestarticle";
import AboutSection from "./components/aboutsection";
import ArticleSpread from "./components/articlespread";
import "./App.css";

class App extends Component {
  state = {
    Spread1: {
      title: "The invisible power that connects us all through social media",
      content:
        "But in today’s world, networking is a necessity. A mountain of research shows that professional networks lead to more job and business opportunities, broader and deeper knowledge, improved capacity to innovate, faster advancement, and greater status and authority. Building and nurturing professional relationships also improves the quality of work and increases job satisfaction.",
    },
    Spread2: {
      title: null,
      content: null,
    },
    Spread3: {
      title: null,
      content: null,
    },
    Spread4: {
      title: null,
      content: null,
    },
  };
  render() {
    for (let i = 0; i < this.state.Spread1.length; i++) {
      console.log(this.state.Spread1[i]);
    }
    return (
      <div
        id="wrapper"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, 430px)",
          gridTemplateRows: "700px 600px 400px 400px 400px",
          gridColumnGap: 200,
        }}
      >
        <div>
          <NameIntro />
        </div>
        <div>
          <LatestArticle />
        </div>
        <div id="aboutSection">
          <AboutSection />
        </div>
      </div>
    );
  }
}

export default App;
