import React, { Component } from "react";
import { motion } from "framer-motion";

const lineExtend = {
  rest: {
    width: 0,
    transition: {
      duration: 1,
    },
  },
  hover: {
    width: 120,
    transition: {
      duration: 1,
    },
  },
};

const textHighlight = {
  rest: {
    transition: {
      duration: 1,
    },
  },
  hover: {
    backgroundImage:
      "linear-gradient(to right, rgba(252, 186, 3, .65) 100%, white 0%)",
    transition: {
      duration: 1,
    },
  },
  leave: {
    transition: {
      duration: 1,
    },
  },
};

class LatestArticle extends Component {
  render() {
    return (
      <div
        id="latestArticle"
        style={{
          display: "grid",
          gridTemplateColumns: "1fr",
          gridTemplateRows: "1fr 140px 1fr",
          gridTemplateAreas: `"." "desc" "latestTopic"`,
        }}
      >
        <div
          style={{
            height: 20,
            width: 20,
            backgroundColor: "black",
            display: "flex",
            alignItems: "center",
            gridArea: "desc",
          }}
        >
          <div
            style={{
              fontSize: 30,
              fontFamily: "Unna",
              fontStyle: "italic",
              fontWeight: "bold",
              position: "relative",
              left: "150%",
            }}
          >
            latest
          </div>
        </div>
        <motion.a
          href="https://www.livemint.com/news/india/hope-and-anxiety-among-liberalization-s-children-11577803916064.html"
          target="_blank"
          style={{
            textDecoration: "none",
            gridArea: "latestTopic",
            color: "black",
          }}
          initial="rest"
          whileHover="hover"
          animate="rest"
        >
          <motion.span
            variants={textHighlight}
            style={{
              fontFamily: "Unna",
              fontWeight: "bold",
              fontSize: 23,
              position: "relative",
              bottom: 40,
            }}
          >
            The secret behind <br /> millennial support
            <br />
            for India's Modi
            <motion.div
              variants={lineExtend}
              style={{
                position: "relative",
                backgroundColor: "black",
                width: 0,
                height: 1,
                top: 10,
              }}
            ></motion.div>
          </motion.span>
          <div
            style={{
              display: "grid",
              gridAutoColumns: "inherit",
              gridColumn: "main1",
              fontFamily: "Roboto, sans-serif",
              fontWeight: 100,
              lineHeight: 1.36,
            }}
          >
            "It is not that young Indians did not have a choice in this
            election. They had plenty. But in the end, the allure of a decisive
            leader proved more important than the BJP's economic stumbles"
          </div>
        </motion.a>
      </div>
    );
  }
}

export default LatestArticle;
