import React, { Component } from "react";
import { motion } from "framer-motion";

const lineExtend = {
  rest: {
    width: 0,
    transition: {
      duration: 1,
    },
  },
  hover: {
    width: 120,
    transition: {
      duration: 1,
    },
  },
};

const textHighlight = {
  rest: {
    transition: {
      duration: 1,
    },
  },
  hover: {
    backgroundImage:
      "linear-gradient(to right, rgba(252, 186, 3, .65) 100%, white 0%)",
    transition: {
      duration: 1,
    },
  },
  leave: {
    transition: {
      duration: 1,
    },
  },
};

class ArticleSpread extends Component {
  state = {
    title: this.props.title,
    desc: this.props.content,
    link: this.props.link,
  };
  render() {
    return (
      <div>
        <motion.a
          href={this.state.link}
          target="_blank"
          style={{
            textDecoration: "none",
            gridArea: "latestTopic",
            color: "black",
          }}
          initial="rest"
          whileHover="hover"
          animate="rest"
        >
          <div style={{ width: "250px" }}>
            <motion.span
              variants={textHighlight}
              style={{
                fontFamily: "Unna",
                fontWeight: "bold",
                fontSize: 30,
                position: "relative",
              }}
            >
              {this.state.title}
              <motion.div
                variants={lineExtend}
                style={{
                  position: "relative",
                  backgroundColor: "black",
                  width: 0,
                  height: 1.2,
                  top: 10,
                }}
              ></motion.div>
            </motion.span>
          </div>
          <div
            style={{
              display: "relative",
              gridAutoColumns: "inherit",
              gridColumn: "main1",
              fontFamily: "Roboto, sans-serif",
              fontWeight: 100,
              lineHeight: 1.36,
            }}
          >
            {this.state.desc}
          </div>
        </motion.a>
      </div>
    );
  }
}

export default ArticleSpread;
