import React, { Component } from "react";
import NavComplete from "../components/navcomplete";

class ArticleFull extends Component {
  state = {
    title: this.props.title,
    description: this.props.description,
  };
  render() {
    return (
      <div>
        <NavComplete />
        <div
          id="Full"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(100px, 700px))",
          }}
        >
          <div>
            <span
              style={{ fontFamily: "Unna", fontWeight: "bold", fontSize: 50 }}
            >
              {this.state.title}
            </span>
          </div>
          <div style={{ fontFamily: "Roboto", fontSize: 20 }}>
            {this.state.description}
          </div>
        </div>
      </div>
    );
  }
}

export default ArticleFull;
