import React, { Component } from "react";

class AboutPageText extends Component {
  state = {
    description: this.props.desc,
  };
  render() {
    const breakLines = (e) => {
      e.split("\n");
      return e;
    };
    console.log(breakLines(this.state.description));

    return <div>{this.state.description}</div>;
  }
}

export default AboutPageText;
