import React, { Component } from "react";
import NameIntro from "./components/nameintro";
import LatestArticle from "./components/latestarticle";
import AboutSection from "./components/aboutsection";
import ArticleSpread from "./components/articlespread";
import "./App.css";

class App extends Component {
  state = {
    content: [
      { id: 1, title: "Title1", summary: "Summary1" },
      { id: 2, title: "Title2", summary: "Summary2" },
      { id: 3, title: "Title3", summary: "Summary3" },
      { id: 4, title: "Title4", summary: "Summary4" },
    ],
  };
  render() {
    return (
      <div
        id="wrapper"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, 430px)",
          gridTemplateRows: "700px 600px 400px 400px 400px",
          gridColumnGap: 200,
        }}
      >
        <div>
          <NameIntro />
        </div>
        <div>
          <LatestArticle />
        </div>
        <div id="aboutSection">
          <AboutSection />
        </div>
        <React.Fragment>
          {this.state.content.map((counter) => (
            <div>
              <ArticleSpread
                key={counter.id}
                title={counter.title}
                content={counter.summary}
              />
            </div>
          ))}
        </React.Fragment>
      </div>
    );
  }
}

export default App;
