import React, { Component } from "react";

class AboutPageText extends Component {
  state = {
    description: this.props.desc,
  };
  render() {
    const breakLines = (e) => {
      e.split("\n").map(function (item, key) {
        return (
          <span key={key}>
            {item}
            <br />
          </span>
        );
      });
    };

    return <div></div>;
  }
}

export default AboutPageText;
