import React, { Component } from "react";
import NavComplete from "../components/navcomplete";
import ArticleText from "./articletext";
import DateAuthor from "./dateauthor";

class ArticleFull extends Component {
  state = {
    title: this.props.title,
    description: this.props.description,
    image: this.props.image,
  };
  render() {
    return (
      <div>
        <NavComplete />
        <div
          id="Full"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(100px, 700px))",
            justifyContent: "center",
          }}
        >
          <div style={{ marginBottom: 20, marginTop: 50 }}>
            <span
              style={{
                fontFamily: "Unna",
                fontWeight: "bold",
                fontSize: 50,
              }}
            >
              {this.state.title}
            </span>
          </div>
          <div style={{ marginBottom: 40 }}>
            <DateAuthor />
          </div>
          <div>
            <img
              src={this.props.image}
              style={{ maxWidth: 500, width: "100%" }}
            />
          </div>
          <div>
            <ArticleText desc={this.state.description} />
          </div>
        </div>
      </div>
    );
  }
}

export default ArticleFull;
