import React, { Component } from "react";
import NavComplete from "../components/navcomplete";
import AboutTitle from "../components/abouttitle";
import AboutPageText from "../components/aboutpagetext";

class AboutPage extends Component {
  state = {
    Data: {},
  };
  componentDidMount = () => {
    fetch("data/aboutpagecontent.json")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ Data: data });
      });
  };
  render() {
    return (
      <div id="aboutwrapper">
        <NavComplete />
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(100px, 500px))",
            justifyContent: "center",
            marginTop: 150,
            gridGap: 90,
          }}
        >
          <div>
            <AboutTitle />
          </div>
          <div id="aboutpagetext">
            <AboutPageText desc={this.state.Data.description} />
          </div>
        </div>
        <div
          style={{
            position: "absolute",
            bottom: -20,
            justifyContent: "center",
          }}
        ></div>
      </div>
    );
  }
}

export default AboutPage;
