import React, { Component } from "react";
import headshot from "../photos/headshotSmaller.png";

class Nameintro extends Component {
  state = {};
  render() {
    return (
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "300 300",
          gridTemplateRows: "70px 1fr",
        }}
        id="nameIntro"
      >
        <div style={{ fontFamily: "Unna", fontStyle: "italic", fontSize: 120 }}>
          vivan &nbsp;
        </div>
        <div
          style={{
            fontFamily: "Unna",
            fontWeight: "bold",
            fontSize: 120,
          }}
        >
          marwaha
        </div>
        <div
          style={{
            position: "relative",
            fontFamily: "Unna",
            fontStyle: "italic",
            gridColumn: "1/2",
          }}
        >
          Writer, philanthropist,
          <br /> and more. The invisible <br />
          social power of technology. <br />
        </div>
        <img
          src={headshot}
          alt="Image of vivan"
          style={{
            position: "relative",
            width: 480,
            height: 400,
          }}
        />
      </div>
    );
  }
}

export default Nameintro;
