import React, { Component } from "react";
import NavComplete from "../components/navcomplete";
import Data from "../data/allarticles.json";
import ArticleSpread from "../components/articlespread";

class AllArticles extends Component {
  state = {};
  render() {
    return (
      <div>
        <NavComplete />
        <div>
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fit, minmax(100px, 400px))",
              gridTemplateRows: "20px repeat(auto-fit, 400px)",
              justifyContent: "center",
              gridGap: 50,
              marginTop: 100,
            }}
          >
            <div style={{}}>
              <div
                style={{
                  height: 20,
                  width: 20,
                  backgroundColor: "black",
                }}
              ></div>
              <span
                style={{
                  fontSize: 30,
                  fontFamily: "Unna",
                  fontStyle: "italic",
                  fontWeight: "bold",
                  width: 200,
                }}
              >
                All Articles
              </span>
            </div>
            <div style={{ gridColumn: "1", gridRow: "3" }}>
              {Data.content.map((counter) => (
                <div>
                  <ArticleSpread
                    key={counter.id}
                    title={counter.title}
                    content={counter.summary}
                    link={counter.link}
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AllArticles;
